import React from "react";

import MidleContentStyle from "./MidleContentStyle"

import DiagramViewBox from "../../../Diagrams/DiagramViewBox"

//import  {ListActiveConxtext} from "../../../../ListActiveContextProvider"

import Triangle from "../../../SVG/Triangle/Triangle"

class MidleContent extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          scale: 1,
          x: 0,
          y: 0,
          originX: 100,
          originY: 0,
          mouseX: 0,
          mouseY: 0
        };
      }

      static MidleContentStyle;

      //static contextType = ListActiveConxtext;

      _showCard = () =>
      {
          if(this.context.ListActive.length === 0)
          {
            return <h3>MidleContent</h3>
          }

          let object = this.context.ListActive.find(item => item.isSelected === true)

          console.log("this._showCard(context)",this.context)



          return <h3>{object.name}</h3>
      }

      getCoords = (elem) => {
        let box = elem.getBoundingClientRect();
        console.log(this.MidleContentStyle.pageYOffset)
        return {
          top: Math.round(box.top), //pageYOffset
          left: Math.round(box.left)//pageXOffset
        };
      }

      handleOnWhell = (e) =>
      {
        //let count = (e.deltaY/17)
        let target = this.MidleContentStyle//e.target.closest('....'); // Здесь что-то уникальное, что может указать на род. блок

        let scale = this.state.scale


        const wheel = e.deltaY < 0 ? 1 : -1;



        // Compute zoom factor.
        let zoom// = Math.exp(wheel * this.state.scale);
        console.log("wheel = ",wheel)
        if(wheel > 0)
        {
          zoom = scale-0.05
          if(zoom < 0 )
          {
            zoom = 0
          }
        }
        else
        {
          zoom = scale+0.05

        }


        let targetCoords = target.getBoundingClientRect();
        let originX = (e.clientX - targetCoords.left )/ zoom;
        let originY = (e.clientY - targetCoords.top )/ zoom;

        this.setState({scale: zoom, originX: originX,originY:  originY }) //,x: e.screenX, y: e.screenY

       /* switch (e.deltaY > 0) {
            case true:
              console.log('positive',e.deltaY,count);
              let plus = this.state.scale+0.05

              this.setState({scale: zoom,originX: -originX, originY: -originY }) //,x: xCoord, y: yCoord
              break;
            default:
              console.log('negative',e.deltaY,count);
              count *=(-1)

              let minus = this.state.scale-0.05
              if(minus < 0 )
              {
                minus = 0
              }

          }*/
      }

      handleOnMouseMove = (e) =>
      {
        let position = this.getTransformedPoint(e.clientX,e.clientY,this.state.scale)
        this.setState({ mouseX: position.x, mouseY: position.y });
      }


      getTransformedPoint=(x, y,scale)=> {
        let target = this.MidleContentStyle
        let targetCoords = target.getBoundingClientRect();
        const transformedX = (x - targetCoords.left)/ scale;
        const transformedY = (y - targetCoords.top)/ scale;
        return { x: transformedX, y: transformedY };
      }


      handleOnClick = (e) =>{
        let target = this.MidleContentStyle//e.target.closest('....'); // Здесь что-то уникальное, что может указать на род. блок

        let targetCoords = target.getBoundingClientRect();
        let originX;
        let originY;

        let newX;
        let newY;


        console.log(targetCoords)

        if(this.state.scale === 1)
        {
          newX = (e.clientX  - targetCoords.left) ;
          newY = (e.clientY -  targetCoords.top) ;
          originX = 0;
          originY = 0;
        }
        else
        {
          newX = (e.clientX - targetCoords.left)/ this.state.scale;
          newY = (e.clientY -  targetCoords.top)/this.state.scale ;
          originX = newX;
          originY = newY;
        }

        this.setState({ x: newX, y: newY, originX: originX,originY:  originY }); //
      }


      handleMouseDown = (e) =>{

      }

      //translate("+this.state.x+"px,"+this.state.y+"px)
      //transformOrigin:this.state.xScale+"px "+this.state.yScale+"px"
      //transformOrigin:this.state.originX+"px "+this.state.originY+"px",
      render() {
        return (
            <MidleContentStyle >
                <div style={{width: "100%", height: "100%",position: "relative"}}  onMouseMove={this.handleOnMouseMove} onClick={this.handleOnClick}   onWheel={this.handleOnWhell}  onMouseDown={this.handleMouseDown}>
                  <div   ref={(MidleContentStyle) => { this.MidleContentStyle = MidleContentStyle;}} style={{width: "100%", height: "100%",position: "absolute",transformOrigin:this.state.originX+"px "+this.state.originY+"px",transform:"scale("+this.state.scale+")",boxShadow: "rgba(0,0,0,0.2) 0px 3px 5px -1px,rgba(0,0,0,0.14) 0px 6px 10px 0px,rgba(0,0,0,0.12) 0px 1px 14px 0px"}}>
                    <Triangle   left={0} top={0} width={100} height={100} min={50} max={100} value={0}/>
                    <Triangle   left={200} top={0}  width={100} height={100} min={50} max={100} value={50}/>
                    <img id="crosshair" src="https://interactive-examples.mdn.mozilla.net/media/examples/crosshair.svg" style={{position: "absolute",left: "calc("+(this.state.x-12)+"px)", top: "calc("+(this.state.y-12)+"px)",opacity: "1"}} width="24px"></img>
                    <img id="crosshair" src="https://interactive-examples.mdn.mozilla.net/media/examples/crosshair.svg" style={{ backgroundColor:"green",position: "absolute",left: "calc("+(this.state.originX-12)+"px)", top: "calc("+(this.state.originY-12)+"px)",opacity: "1"}} width="24px"></img>
                    <img id="crosshair" src="https://interactive-examples.mdn.mozilla.net/media/examples/crosshair.svg" style={{backgroundColor:"blue",position: "absolute",left: "calc("+(this.state.mouseX-12)+"px)", top:"calc("+(this.state.mouseY-12)+"px)",opacity: "1"}} width="24px"></img>
                  </div>



                </div>


               {/* <DiagramViewBox/>

              <ListActiveConxtext.Consumer>
                {value => (
                  <h3>MidleContent {console.log(value.ListActive)}</h3>
                )}

                </ListActiveConxtext.Consumer>*/} 
            </MidleContentStyle>

        )
      }
}



export default MidleContent;

