import React from "react";
import CardListOrderStyle from "./CardListOrderStyle"
import styled from 'styled-components'


import Time from "../Time/Time"

const ButtonAdd = styled.button`
width: 100%;
border: none;
background-color: transparent;
animation: ${props => props.addClick ? "fade-in-keyframes 0.5s" : ""};
    @keyframes fade-in-keyframes {
        from {
            opacity: 0
        }
        to {
            opacity: 1
        }
    }
cursor: pointer;
    &:hover {
        background-color: lightgrey;
    }
`;


const TimeDefault = styled.div`
    width: 50%;

    border-right: 1px solid rgb(208, 208, 208);
`;

const TimeActual = styled.div`
    width: 50%;
`;


class CardListOrder extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,

          Title: props.Title,
          count: props.count,
          focus: props.focus,
          addClick: true
        };
    }

    static getDerivedStateFromProps(props, state) {
        if (props.Title !== state.Title) {
          return {
            Title: props.Title,
            count: props.count,
          };
        }
        return null;
    }

    handleAnimationEnd = (e) => {
        this.setState({addClick: false})
    }

    Sim = (e) => {
        this.setState({addClick: true})
    }

    Show = () => {
        console.log("isLoaded ",this.state.isLoaded)
        return this.state.ListActive.map((item,index) =>{
            return (
                <CardListOrderStyle key={index}>
                    <div style={{borderBottom: "1px solid #D0D0D0", display: "flex",flexDirection: "row", justifyContent: "center",direction: "ltr"}}>
                        <p style={{padding: "0",margin: "0",color:"white" }}>{item.title}</p>
                    </div>
                    <div>
                    <div style={{display: "flex", direction:"ltr", margin:"0",borderBottom: "1px solid rgb(208, 208, 208)"}}>
                        <TimeDefault>Part 1</TimeDefault>
                        <TimeActual> Part 2</TimeActual>
                    </div>
                    <ButtonAdd  addClick={this.state.addClick}
                    onClick={this.Sim} onAnimationEnd={this.handleAnimationEnd} >Start Simulation</ButtonAdd>
                    </div>
                </CardListOrderStyle>
            )
          //return <p key={index}>{item.title +" "+item.count}</p>
        })
    }

    Select = (e)=> {
        this.setState({focus: !this.state.focus})
    }

    render() {
        return (
            <CardListOrderStyle onClick={this.Select} focus={this.state.focus}>
                <div style={{borderBottom: "1px solid #D0D0D0", display: "flex",flexDirection: "row", justifyContent: "space-between",direction: "ltr", zIndex:"2"}}>
                    <p style={{padding: "0",margin: "0" }}>{this.state.Title}</p>
                    <p style={{padding: "0",margin: "0" }}>{this.state.count+"  шт"}</p>
                </div>
                <div>
                {/*<div style={{display: "flex", direction:"ltr", margin:"0",borderBottom: "1px solid rgb(208, 208, 208)"}}>
                    <TimeActual> Part 1</TimeActual>
                    <TimeActual> Part 2</TimeActual>
                </div>*/}
                <Time/>
                <ButtonAdd  addClick={this.state.addClick}
                onClick={this.Sim} onAnimationEnd={this.handleAnimationEnd} >Start Simulation</ButtonAdd>
                </div>
                <div>
                    DIMO
                </div>
            </CardListOrderStyle>
        )
    }
}

export default CardListOrder;
