import React from 'react';

import ListItem from "./ListItem"

import  {Conxtext,ListActiveConsumer} from "../../ListActiveContextProvider"

class ListView extends React.PureComponent {
    constructor(props) {
      super(props);
      this.escFunction = this.escFunction.bind(this);
      this.state = {
        selected: false,
      }
    }

    static contextType = Conxtext

    _showChoices = () => {
      return this.context.ListActive.map((item, i) => {
        return (
            <ListItem
                title={item.choice_name}
                checked={item.isChecked}
                size={50}
                onIconPress={() => {}}
                onPress={() => {

                  this.context.ListActive.map((item2,j) =>{
                    item2.isSelected = false;
                  });
                  this.context.ListActive[i].isSelected = true;
                  this.setState({ selected: !this.state.selected});

                }}
                isSelected={this.context.ListActive[i].isSelected}
                key={item.choice_id}
                MyKey = {item.choice_id}
            />
        );
      });
    };

    escFunction(event){
      console.log(event.keyCode)
      if(event.keyCode === 38) {
        //Do whatever when esc is pressed

       // this.context.


       let list = this.context.ListActive;

       let object = list.find(item => item.isSelected === true)

       console.log("dsfasdf",object)

       if(object === undefined)
       {
         return;
       }

       let position = list.indexOf(object)

       console.log("position = ",position)

       if(position-1 < 0)
       {
         return;
       }

       if(position < list.length )
       {
         object.isSelected = false;
         list[position-1].isSelected = true;
       }
       else
       {
        object.isSelected = false;
       }

       console.log("dsfasdf",this.context.ListActive,object,position)


      this.setState({ selected: !this.state.selected});


      }
    }
    componentDidMount(){
      console.log("componentDidMount")
      document.addEventListener("keydown", this.escFunction, false);
    }
    componentWillUnmount(){
      console.log("componentWillUnmount")
      document.removeEventListener("keydown", this.escFunction, false);
    }

    render() {
      return (
        <div>
          <ListActiveConsumer>
          {context => (
            <ul>{this._showChoices()}</ul>
          )}
          </ListActiveConsumer>  
        </div>
      );
    }
  }

  export default ListView;
