import React,{useEffect, useState} from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { CardHeader, IconButton } from '@material-ui/core';
import SettingsIcon from '@material-ui/icons/Settings';
import { Divider } from '@mui/material';

import {IDataColorOrderInDefault} from '../../Data/DataCardOrderInDefault/IDataColorOrderInDefault'
import IDataOrder from '../../Data/IDataOrder';

enum simulationEnum{
  Start = 'Запустить симуляцию',
  Stop = 'Остановить симуляцию'
}
const CardOrderInDefault = (props:{OrderColor: IDataColorOrderInDefault, data: IDataOrder}) => {

  const [simulation, setSimulation] = useState({
    text: simulationEnum.Start,
    color: IDataColorOrderInDefault.NotWork,
    work: false
  });

  const {Start,Stop} = simulationEnum;

  const onSimulation = () => {

    if(simulation.work)
    {
      setSimulation({...simulation, work:!simulation.work, text: Start })
    }
    else
    {
      setSimulation({...simulation, work:!simulation.work, text: Stop })
    }
  }

  let flag = (simulation.text === simulationEnum.Stop && simulation.work)


  useEffect(() => {
    // Обновляем название докуммента, используя API браузера
  });


  return (
    <Card sx={{ minWidth: 200,color: flag ? 'white':'black',  backgroundColor: props.OrderColor }}>
      <CardHeader
        titleTypographyProps={{ variant: "h6" }}
        action={
         <div>
          <IconButton aria-label="settings">
            <SettingsIcon />
          </IconButton>
          <IconButton aria-label="settings">
            <SettingsIcon />
          </IconButton>
         </div>
        }
        title={"Заказ номер: "+props.data.name}
        subheader={<Typography style={{color: flag ? 'white':'black'}}>23:24:23</Typography>}
      />
      <Divider/>
      <CardActions>
        <Button size="small" color={simulation.work ? "success":"info"} variant={simulation.work ? "contained":"outlined"} onClick={onSimulation}>{simulation.text}</Button>
      </CardActions>
    </Card>
  );
}

export default CardOrderInDefault;
