import {useLeftMenuType,useLeftMenuAction,useLeftMenuActionType } from '../../type/useLeftMenuType'

const initialState: useLeftMenuType ={
    drawerWidth: 240,
    show: false,
    error: null
}

export default useLeftMenuReducer =(state = initialState, action:useLeftMenuAction): useLeftMenuType => {
    switch(action.type)
    {
        case useLeftMenuActionType.LEFT_SHOW:
            return {drawerWidth: initialState.drawerWidth, show: action.pyload, error: initialState.error}
        break;
        case useLeftMenuActionType.LEFT_HIDDEN:
            return {drawerWidth: initialState.drawerWidth, show: action.pyload, error: initialState.error}
        break;
        default:
            return state;
    }
}
