import axios from "axios"
import { Dispatch } from "react"
import IDataOrder from "../../Component/Data/IDataOrder"
import { useOrdersActionType,useLeftMenuAction } from "../../type/useOrdersType"

import { fetch_orders, fetch_orders_success, fetch_orders_error,fetch_orders_add,fetch_orders_update } from '../reducers/useOrdersReducer'


import IDialogAddOrder from '../../Component/Data/Dialog/IDialogAddOrder'

export const ordersLoading = () => {
    return async (dispatch: any) => {

        console.log("Loading")
        try{
            dispatch(fetch_orders());
            //const response = await axios.get('https://jsonplaceholder.typicode.com/users')

            const response = await axios.get('https://localhost:5001/api/manufacture/orders',{
                headers: {
                  "Access-Control-Allow-Origin": "*",
                }
            })


            let data = response.data as IDataOrder[];
            console.log("Loading finish")
            console.log(data)
            setTimeout(() => {
                dispatch(fetch_orders_success(data));
            }, 0)
        } catch(e) {
            dispatch(fetch_orders_error('Error'));
        }
    }
}


export const orderAdd = (newOrder: IDialogAddOrder) => {
    return async (dispatch: any) => {

        console.log("Add")
        console.log(newOrder)
        console.log(JSON.stringify(newOrder))
        try{

            //const response = await axios.get('https://jsonplaceholder.typicode.com/users')

            const response = await axios.post('https://localhost:5001/api/manufacture/addOrder',JSON.stringify(newOrder),{
                headers: {
                  // Overwrite Axios's automatically set Content-Type
                  "Access-Control-Allow-Origin": "*",
                  'Content-Type': 'application/json'
                }
              })

            if(response.status === 200)
            {
                dispatch(fetch_orders_add(newOrder));
                console.log("Add Ok")
            }
        } catch(e) {
            dispatch(fetch_orders_error('Error'));
        }
    }
}

export const orderUpdate = (updateOrder: IDataOrder, oldOrder: IDataOrder) => {
    return async (dispatch: any) => {
        let obj = {newOrder: updateOrder , oldOrder: oldOrder}
        try{
            const response = await axios.post('https://localhost:5001/api/manufacture/updateOrder',JSON.stringify(obj),{
                headers: {
                  "Access-Control-Allow-Origin": "*",
                  'Content-Type': 'application/json'
                }
              })

            if(response.status === 200)
            {
                dispatch(fetch_orders_update(obj));
                console.log("Add Ok")
            }
        } catch(e) {
            dispatch(fetch_orders_error('Error'));
        }
    }
}


export const startSimulation = (item: IDataOrder) => {
    return async (dispatch: any) => {

        let copyObjet = {...item}
        try
        {
            const response = await axios.post('https://localhost:5001/api/manufacture/startSimulationOrder',JSON.stringify(copyObjet),
            {
                headers: {
                   "Access-Control-Allow-Origin": "*",
                  'Content-Type': 'application/json'
                }
            })

            if(response.status === 200)
            {
                //dispatch(fetch_orders_update(obj));
                console.log("Add Ok")
            }
        } catch(e) {
            dispatch(fetch_orders_error('Error'));
        }
    }
}


export const updateInfoOrder = (item: IDataOrder) => {
    return async (dispatch: any) => {

        //let copyObjet = {...item}
        try
        {
            const response = await axios.post('https://localhost:5001/api/manufacture/updateInfoOrder',JSON.stringify(item),
            {
                headers: {
                  "Access-Control-Allow-Origin": "*",
                  'Content-Type': 'application/json'
                }
            })

            if(response.status === 200)
            {

                console.log("Update")
                let data =  JSON.parse(response.data) as IDataOrder;

                item = data;
                dispatch(fetch_orders_update(data));
                console.log("Update Ok")
            }
        } catch(e) {
            dispatch(fetch_orders_error('Error'));
        }
    }
}
