import React from "react";
import LeftListActiveStyle from "./LeftListActiveStyle"

//import ListActive from "./ListActive";

//import ListActive from "./ListActive"

import CardListOrder from "../../../CardListOrder/CardListOrder"

class LeftListActive extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          ListActive: this.props.ListActive,
          Select: false
        };
      }

      Select = (keyIndex) => {
        let  ListActive  = JSON.parse(JSON.stringify(this.state.ListActive))
        ListActive.map((item,index) =>{
          if(index === keyIndex)
          {
            item.focus = true;
          }
          else
          {
            item.focus = false;
          } 
        })
        this.setState({ListActive: ListActive,Select: !this.state.Select})
      }

      Test = () => {
        return this.state.ListActive.map((item,index) =>{
          return <CardListOrder key={index} index={index} Title={item.title} count={item.count} focus={item.focus} Select={this.Select}/>
        })
      }

      /*static getDerivedStateFromProps(props, state) {

