          if(state.Select)
          {
            return {
              ListActive: state.ListActive,
              Select : false,
            };
          }

          return {
            ListActive: props.ListActive,
          };
        }
        return null;
      }*/

      render() {
        console.log("ListActive = ",this.state.ListActive)
        return (
            <LeftListActiveStyle>
                {this.Test()}
                {/*<ListActive/>*/}
            </LeftListActiveStyle>
        )
      }
}
//<h3>LeftListActiveStyle</h3>


export default LeftListActive;
