import React from 'react';

import ListItem from "./ListItem"

import  {ListActiveContextProvider} from "../../Global"

class ListView extends React.PureComponent {
    constructor(props) {
      super(props);

      this.state = {
          list: []
      }
    }




    _showChoices = () => {
      return this.state.list.map((item, i) => {
        return (
            <ListItem
                title={item.choice_name}
                checked={item.isChecked}
                size={50}
                onIconPress={() => {}}
                onPress={() => {
                //let clonedList = this.state.list.slice();
                //clonedList[i].isChecked = !clonedList[i].isChecked;
                //this.setState({ list: clonedList });
                }}
                key={item.choice_id}
                MyKey = {item.choice_id}
            />
        );
      });
    };

    componentDidMount()
    {

    }

    render() {
    const [ListActive, setContext] = useContext(ListActiveContext);
    this.setState({list: ListActive})
      return (
        <div>
          <ListActiveContextProvider>
          {context => (
            <ul>{this._showChoices(context.ListActive)}</ul>
          )}
          </ListActiveContextProvider>  
        </div>
      );
    }
  }

  export default ListView;
