import React from "react";
//import styled from 'styled-components'


class Triangle extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
		  width: this.props.width,
		  height: this.props.height,
		  percent: this.props.percent
        };
      }

	   findPoint =(percent,x,y)=>
		{
			let H_pointPx = (y*percent)/100 // Нашли сколько же пикселей занимает по высоте наши проценты
			let pointPx = H_pointPx // находим точку по высоте если считать снизу

			return this.findPointSin(pointPx,x/2,y) // находим длину по X
		}

		findPointSin = (b,a1,b1) =>
		{
			return (b*a1)/b1
		}

		returnStr=()=>
		{
			let x = this.state.width;
			let y = this.state.height;
			let percent = this.state.percent
			let point_Y = y - (y*percent)/100;
			let point_X = this.findPoint(percent,x,y)
			let percentByze = 10;

			percentByze = percentByze*percent/100

			if(percent > 70)
			{
				percentByze = 5;	
			}

			let byze = point_Y+((y*percentByze)/100) // плюс 10%


			let point_Y_2 = point_Y
			let point_X_2 = x - point_X

			return ("M0,"+y+" "+point_X+","+point_Y+" Q "+x/2+","+byze+" "+point_X_2+","+point_Y_2+" "+point_X_2+","+point_Y_2+" "+x+","+y)
		}

		componentDidMount() {
			this.interval = setInterval(() => {
				let newValue = this.state.percent+1;
				if(newValue > 100)
				{
					newValue = 0;
				}
				this.setState({percent: newValue}) //Math.floor(Math.random() * this.state.height)
			},100)
		}

		componentWillUnmount() {
			clearInterval(this.interval);
		  }

      render() {
		let {width,height} = this.state
		let viewBox = "0 0 "+width+" "+height;
		let pointsPolygon = "0,"+height+" "+width/2+",0 "+width+" "+height;
		let D = this.returnStr()
        return (
			<div>
				<p>{this.state.percent}</p>
				<svg xmlns="http://www.w3.org/2000/svg" width={width} height={height} viewBox={viewBox} fill="none" stroke="#000000" strokeWidth="0.5" strokeLinecap="round" strokeLinejoin="round">
				  <polygon points={pointsPolygon} fill="lightgray" stroke="lightgray" strokeWidth="1"></polygon>
				  <g><path d={D} fill="green" stroke="green"></path></g>
				</svg>

			</div>
        )
      }
}


export default Triangle;
