import React, { useState, useEffect, useRef } from "react";

const URL = 'ws://192.168.0.2:5001/ws';
const Chat =() => {
	const [user, setUser] = useState('Tarzan');
  	const [message, setMessage] = useState([]);
  	const [messages, setMessages] = useState([]);
  	const [ws, setWs] = useState(new WebSocket(URL));

  	const submitMessage = (usr, msg) => {
  		const message = { user: usr, message: msg };
  		ws.send(JSON.stringify(message));
  		setMessages([message, ...messages]);
  	}

    const soket = useRef(null)

  /*	useEffect(() => {
	    soket.onopen = () => {
	      console.log('WebSocket Connected');
	    }

	    soket.onmessage = (e) => {
          console.log('Read');
	      const message = JSON.parse(e.data);
	      setMessages([message, ...messages]);
	    }

	    return () => {
            soket.onclose = () => {
	        console.log('WebSocket Disconnected');
	        setWs(new WebSocket(URL));
	      }
	    }
  	}, ); //[ws.onmessage, ws.onopen, ws.onclose, messages]
*/

      const [isPaused, setPause] = useState(false);

      useEffect(() => {
          console.log("can try create connection")
          ws.current = new WebSocket(URL);
          ws.current.onopen = () => console.log("ws opened");
          ws.current.onclose = () => console.log("ws closed");

          const wsCurrent = ws.current;

          return () => {
              wsCurrent.close();
              setWs(ws);
          };
      }, []);

      useEffect(() => {
          if (!ws.current) return;

          ws.current.onmessage = e => {
              if (isPaused) return;
              const message = JSON.parse(e.data);
              console.log("e", message);
              setMessages([message, ...messages]);
          };
      }, [isPaused]);


  	return (
	    <div>
	        <label htmlFor="user">
	          Name :
	          <input
	            type="text"
	            id="user"
	            placeholder="User"
	            value={user}
	            onChange={e => setUser(e.target.value)}
	          />
	        </label>

	        <ul>
	          {messages.reverse().map((message, index) =>
	            <li key={index}>
	              <b>{message.user}</b>: <em>{message.message}</em>
	            </li>
	          )}
	        </ul>

	        <form
	          action=""
	          onSubmit={e => {
	            e.preventDefault();
	            submitMessage(user, message);
	            setMessage([]);
	          }}
	        >
	          <input
	            type="text"
	            placeholder={'Type a message ...'}
	            value={message}
	            onChange={e => setMessage(e.target.value)}
	          />
	          <input type="submit" value={'Send'} />
	        </form>
            <button onClick={() => setPause(!isPaused)}>
                {isPaused ? "Resume" : "Pause"}
            </button>
	    </div>
  	)
}

export default Chat;
