import * as React from 'react';
import { DataGridPro } from '@mui/x-data-grid-pro';
import { DataGrid, GridColDef, GridValueGetterParams,GridRenderCellParams } from '@mui/x-data-grid';
import moment from 'moment';
import Order_view from '../Data/IDataOrder/IDataOrder';




const DemoArchive = (props:{orders?: Order_view[]}) => {

  const [tableData, setTableData] = React.useState<Order_view[]>(props.orders != undefined ? props.orders : [])


  if(tableData.length > 0 )
  {
    tableData.forEach((element, i) => {
          element.id = i+1;
    });
  }


  const splitTime = (time: string) => {

    return (
      <div>
      {time.split(' ').map((item,index) => {
         return <p key={index} style={{marginBottom: '0', marginTop: '0'}}>{item}</p>
      })}
      </div>
    )
  }

  const columns: GridColDef[] = [
    { field: 'id', headerName: '№', width: 90 },
    {
      field: 'name',
      headerName: 'Заказ',
      width: 120,
      editable: true,
    },
    {
      field: 'tStart',
      headerName: 'Добавлен',
      type: 'number',
      width: 120,
      editable: true,
      renderCell: (params) => (
          splitTime( moment(params?.row.tStart).format('MM/DD/YYYY HH:mm:ss'))
        )
    },

    {
      field: 'tPlan',
      headerName: 'План',
      type: 'number',
      width: 100,
      editable: true,
      renderCell: (params) => (
        splitTime( moment(params?.row.tStart).add(params?.value, 'seconds').format('MM/DD/YYYY HH:mm:ss'))
       )
    },
    {
      field: 'limitation',
      headerName: 'Срок',
      width: 100,
      editable: true,
      renderCell: (params) => (
        splitTime( moment(params?.value).format('MM/DD/YYYY HH:mm:ss'))
        )
    },
    {
      field: 'tFuture',
      headerName: 'Прогноз',
      width: 100,
      editable: true,
      renderCell: (params) => (
        splitTime( moment(params?.value).format('MM/DD/YYYY HH:mm:ss'))
        )
    },


    /*
    {
      field: 'fullName',
      headerName: 'Full name',
      description: 'This column has a value getter and is not sortable.',
      sortable: false,
      width: 160,
      valueGetter: (params: GridValueGetterParams) =>
        `${params.getValue(params.id, 'orderName') || ''} ${
          params.getValue(params.id, 'limitation') || ''
        }`,
    },*/
  ];

  const convertToTime = (t: number) =>
  {
    return moment.unix(t).utc().format('H:m:s')
  }

  const rows = [
    { id: 1, limitation: 'Snow', orderName: 'Jon', tPlan: convertToTime(35) },
    { id: 2, limitation: 'Lannister', orderName: 'Cersei', tPlan: convertToTime(42) },
    { id: 3, limitation: 'Lannister', orderName: 'Jaime', tPlan: convertToTime(45) },
    { id: 4, limitation: 'Stark', orderName: 'Arya', tPlan: convertToTime(16) },
    { id: 5, limitation: 'Targaryen', orderName: 'Daenerys', tPlan: null },
    { id: 6, limitation: 'Melisandre', orderName: null, tPlan: convertToTime(150) },
    { id: 7, limitation: 'Clifford', orderName: 'Ferrara', tPlan: convertToTime(44) },
    { id: 8, limitation: 'Frances', orderName: 'Rossini', tPlan: convertToTime(36) },
    { id: 9, limitation: 'Roxie', orderName: 'Harvey', tPlan: convertToTime(65) },
  ];

  console.log("props.orders = ",props.orders);

  return (
    <div style={{ height: 520, width: '100%' }}>
      <DataGrid

        columns={columns}
        rows={tableData}

        pageSize={5}
        rowsPerPageOptions={[5]}
        checkboxSelection
        disableSelectionOnClick
      />
    </div>
  );
}
export default DemoArchive;
