import React,{useEffect, useState} from 'react'
import { styled } from '@material-ui/core/styles';

import Triangle from './Triangle'

import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader'
import CardContent from '@material-ui/core/CardContent'
import IconButton from '@material-ui/core/IconButton'
import MoreVertIcon from '@material-ui/icons/MoreVert';
import IDataBufferVSM from '../Data/IDataBufferVSM';

import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import {IconButtonProps} from '@material-ui/core/IconButton'

import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import IDataOrderItem from '../Data/IDataOrderItem'
import SettingsIcon from '@material-ui/icons/Settings';
import axios from 'axios'

import ip from '../../global'

interface ExpandMoreProps extends IconButtonProps {
    expand: boolean;
}



const Buffer = (props:{buffer: IDataBufferVSM}) => {

    const Colors = { RED: 'red', GREEN: 'green', YELLOW: 'yellow', WHITE: 'white', Header: '#6495ed'};

     const {
      name,
      max,
      minHold,
      valueDefault,
      type,
      value
    } = props.buffer;

    const ExpandMore = styled((props: ExpandMoreProps) => {
        const { expand, ...other } = props;
        return <IconButton {...other} />;
      })(({ theme, expand }) => ({
        transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      }));

    const [expanded, setExpanded] = React.useState(false);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    const [information, setInformation] = useState({
      buffer: new Object as IDataBufferVSM,
      show: true
    });

    const [color,setColor] = useState('#d3d3d3');

    let Example = {
      orderItems: [
        {
          name : 'sts',
          part: 1,
          order : {
            id: 2,
            quantity: 1,
          }

        } as IDataOrderItem,
        {
          name : 'sts',
          part: 1,
          order : {
            id: 2,
            quantity: 1,
          }

        } as IDataOrderItem,
        {
          name : 'sts',
          part: 1,
          order : {
            id: 2,
            quantity: 1,
          }

        } as IDataOrderItem,
        {
          name : 'sts',
          part: 1,
          order : {
            id: 2,
            quantity: 1,
          }

        } as IDataOrderItem
      ] as IDataOrderItem[],
      buffer : null,
      show: false
    }

    /*
   if(information.orderItems.length <= 0 )
   {
    setInformation(Example as any);
   }*/





   const fetchInfo  = async() =>{
    const response = await axios.post('http://'+ip+':5001/api/manufacture/updateInfoBuf',props.buffer,
    {
        headers: {
          "Access-Control-Allow-Origin": "*",
          'Content-Type': 'application/json'
        }
    })

    if(response.status === 200)
    {
      console.log("BUF = ", response.data as IDataBufferVSM);
    }
   }
   ///

   useEffect(()=>{

    fetchInfo();


   },[])


   ///

    const orderItems = information.orderItems?.map((item: IDataOrderItem, index: number) =>{
        //var time = moment.unix(item.tActual as number).utc().format('H:m:s')
        var time = "00:00:00"

        //borderRight: '1px solid rgba(0, 0, 21, 0.125)'
        return(
          <div key={index} style={{backgroundColor: color}}>
                <p style={{display: 'flex', justifyContent:'center', fontWeight: 'bold'}} >Партия {item.orders_production.id}</p>
                <div style={{display: 'flex', justifyContent:'space-around'}} >
                  <p>№ Текущего заказа</p>
                  <p>Всего заказов</p>
                </div>
                <div style={{display: 'flex', justifyContent:'space-around'}} >
                  <p >{item.part}</p>
                  <p >{item.orders_production.quantity}</p>
                </div>
                <p style={{display: 'flex', justifyContent:'center'}} >Общее время</p>
                <p style={{display: 'flex', justifyContent:'center', fontWeight: 'bold'}}>{time} сек</p>
          </div>
        )
      })


    return(
        <Card sx={{ minWidth: '150px',verticalAlign: 'middle'}}>
            <CardHeader
                action={
                    <IconButton aria-label="settings">
                    <SettingsIcon />
                    </IconButton>
                }
                title={name}
                sx={{fontWeight: 'bold', color:'white', backgroundColor: Colors.Header,paddingBottom: '0px'}}>
            </CardHeader>
            <div  style={{fontWeight: 'bold',fontSize:'1rem',backgroundColor: Colors.Header,color: 'white', display: 'flex', justifyContent: 'space-around'}}>
              <p>Время цикла:</p>
              <p>55</p>
            </div>
            <CardContent style={{padding: 1}}>
                    <p style={{display: 'flex', justifyContent:'center', margin: 0}}> {max}</p>
                    <Triangle buff={{name: name, minHold: minHold, max: max,value: value,valueDefault: valueDefault} as IDataBufferVSM}/>
            </CardContent>
            <CardActions disableSpacing sx={{padding: 0, margin: 0}}>
                <ExpandMore
                    expand={expanded}
                    onClick={handleExpandClick}
                    aria-expanded={expanded}
                    aria-label="show more"
                >
                  <ExpandMoreIcon />
                </ExpandMore>
            </CardActions>
            <Collapse sx={{margin: 0, padding: 0, display: 'grid'}} in={expanded} timeout="auto" collapsedSize={0} unmountOnExit>
              {orderItems}
            </Collapse>
        </Card>
    )

}


export default Buffer;
