import {useLeftMenuType,useLeftMenuAction,useLeftMenuActionType } from '../../type/useLeftMenuType'

const initialState: useLeftMenuType ={
    drawerWidth: 240,
    show: false,
    error: null
}

export const  useLeftMenuReducer =(state = initialState, action:useLeftMenuAction): useLeftMenuType => {
    switch(action.type)
    {
        case useLeftMenuActionType.LEFT_SHOW:
            initialState.show = true;
            return initialState
        break;
        case useLeftMenuActionType.LEFT_HIDDEN:
            initialState.show = false;
            return initialState
        break;
        case useLeftMenuActionType.SET_WIDTH:
            initialState.drawerWidth = action.pyload;
            return initialState
        break;
        default:
            return state;
    }
}


export const showMenu = () => ({type: useLeftMenuActionType.LEFT_SHOW})
export const hiddenMenu = () => ({type: useLeftMenuActionType.LEFT_HIDDEN})
export const setWidthMenu = () => ({type: useLeftMenuActionType.LEFT_HIDDEN})
