import  React from 'react'
import TextField from '@material-ui/core/TextField';
import {useLocation, useHistory} from 'react-router'

import IDataOrder from '../Data/IDataOrder';

import MenuItem from '@material-ui/core/MenuItem';
import OrderRole from '../Data/OrderRole';
import Button from '@material-ui/core/Button';
import DialogActions from '@material-ui/core/DialogActions';

interface stateType{
  data: IDataOrder
}




const PageInformationAboutOrder = () =>
{
  const location = useLocation<stateType>()
  const history = useHistory()

  const [data, setData] = React.useState<IDataOrder>();

  const handleChangeName = (event: React.ChangeEvent<HTMLInputElement>) => {

  };

  const handleChangePriority = (event: React.ChangeEvent<HTMLInputElement>) => {

  };

  const handleChangeOrderRole = (event: React.ChangeEvent<HTMLInputElement>) => {

  };


  // Helper
const StringIsNumber = (value: any) => isNaN(Number(value)) === false;

// Turn enum into array
function ToArray(enumme: any) {
    return Object.keys(enumme)
        .filter(StringIsNumber)
        .map(key => enumme[key]);
}

let listRole = ["Актуальный", 'В работе', 'Архив'];

  if(location.state.data != null)
  {
    if(data === undefined)
    {
      setData(location.state.data);
      return <p>Загрузка</p>
    }

    return (
      <div>
         <TextField
          onChange={handleChangeName}
          autoFocus
          margin="dense"
          id="name"
          label="Название"
          type="name"
          fullWidth
          variant="standard"
          value={data?.name}
        />
        <TextField
          onChange={handleChangePriority}
          autoFocus
          margin="dense"
          id="priority"
          label="Приоритет"
          type="priority"
          fullWidth
          variant="standard"
          value={data?.priority}
        />

        <TextField
          id="standard-select-currency"
          select
          label="Выбирите роль"
          value={listRole[data?.orderRole-1]}

          helperText="Выбирите продукт"
          variant="standard"
        >

          { ["Актуальный", 'В работе', 'Архив'].map((option, index) => (
            <MenuItem key={index} value={option}>
              {option}
            </MenuItem>
          ))}
        </TextField>
        <DialogActions>
          <Button >Отменить</Button>
          <Button >Сохранить</Button>
      </DialogActions>
      </div>
  )   

  }

  return <p>Нет данных</p>


}

export default PageInformationAboutOrder;
