import React from "react";
import CardAddOrderStyle from "./CardAddOrderStyle"

import styled from 'styled-components'

const FocuseStyle = styled.li`
height: .25rem;
box-shadow: ${props => props.focus ? "0 .25rem 0 0 #ffcd1f":"0 1px 0 0 #D0D0D0"};
transition: all .12s cubic-bezier(.47,0,.745,.715);
`;

class CardAddOrder extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          value: 12345,
          focus: false
        };
      }


      handleLeftArrowDown = (e) => {
        let input_object = e.target.nextElementSibling;

        let value = Number(input_object.value) - 1;
        if(value < input_object.min)
        {
            input_object.value = input_object.min
        }
        else
        {
            input_object.value = value
        }
        this.setState({value: input_object.value})
     }

     handleRightArrowUP = (e) => {
        let input_object = e.target.previousElementSibling; //this.previousElementSibling.previousElementSibling.stepDown()

        let value = Number(input_object.value) + 1;
        input_object.value = value

        this.setState({value: input_object.value})
     }

     handleChange = (e) => {
         console.log("handleChange")
     }


    insert = (arr, index, ...newItems) => [
        // part of the array before the specified index
        ...arr.slice(0, index),
        // inserted items
        ...newItems,
        // part of the array after the specified index
        ...arr.slice(index)
      ]

     handleKeyDown = (e) => {
       // let flag = !(/^[А-Яа-яA-Za-z]$/.test(e.key)) 


       switch(e.key)
       {
           case "Backspace":
            {
                e.preventDefault()
                var pos = e.target.selectionStart-1;

                //let value = e.target.value.substring(0, e.target.value.length - 1)

                let value_part1 = e.target.value.substring(0,pos)
                let value_part2 = e.target.value.substring(pos+1,e.target.value.length)

                let value = value_part1 + value_part2



                e.target.value = value
                e.target.setSelectionRange(pos, pos)
                this.setState({value: value})

               break;
           }

           case "ArrowUp":
           {
               this.handleRightArrowUP(e)
               this.setState({value: e.target.value})
               break;
           }
           case "ArrowDown":
           {
               e.target.stepDown()
               this.setState({value: e.target.value})
               break;
           }
           case "Enter":
           case "Meta":
           case "Alt":
           case "Control":
           case "CapsLock":
           case "Shift":
           case "Tab":
           case "ArrowLeft":
           case "ArrowRight":
           {
               break;
           }
           default:
           {
                let flag = !(/\D/g.test(e.key)) 

                console.log(e.key,Number(this.state.value),Number(e.target.min),Number(e.target.value) < Number(e.target.min),flag)

                if(flag)
                {
                    if(Number(e.target.value) === 0 && Number(e.key) < Number(e.target.min))
                    {
                        e.target.value = e.target.min
                        this.setState({value: Number(e.target.min)})
                    }
                    else
                    {
                        e.preventDefault()
                        var pos = e.target.selectionStart;
                        e.target.value = this.insert(e.target.value,pos,e.key.toString()).join("")
                        e.target.setSelectionRange(pos+1, pos+1)

                        this.setState({value: Number(e.target.value)})
                    }

                }
                else
                {
                    if( (e.target.value.length-1) < 0)
                    {
                        this.setState({value: Number(e.target.min)})
                    }
                    else
                    {
                        e.target.value = this.state.value;
                    }
                }



               break;
           }
       }

    }

    Focus = () =>{
         this.setState({focus: true})
    }

    Blur = () =>{
        this.setState({focus: false})
    }

      render() {
        return (
            <CardAddOrderStyle>
                <div style={{borderBottom: "1px solid #D0D0D0", display: "flex",flexDirection: "row",
  justifyContent: "center",direction: "ltr"}}>
                    <p style={{padding: "0",margin: "0"}}>Title</p>
                </div>
                <div style={{display: "flex", direction:"ltr", alignItems: "center"}}>
                  <ul style={{width: "80%", margin:"0"}}>


                  <span id="raz">
                    <span id="leftArrow" onClick={this.handleLeftArrowDown}></span>
                    <input style={{border: "none",  outline: "none", backgroundColor: "transparent", fontSize:"14px",width: "100%",textAlign: "center",padding:"0" }} type="text" min="5" value={this.state.value} onKeyDown={this.handleKeyDown} onFocus={this.Focus} onBlur={this.Blur}/>
                    <span id="rightArrow" onClick={this.handleRightArrowUP}></span>
                  </span>

                    <FocuseStyle focus={this.state.focus}/>

                    <li style={{color:"#c3c7d5", textAlign: "inherit",fontWeight: "600",fontSize: ".75rem", letterSpacing: ".0625rem",lineHeight: "1.333333333333333",paddingTop:"3%"}}>
                          Количество
                    </li>
                  </ul>
                  <ul style={{width: "20%"}}>+</ul>
                </div>
            </CardAddOrderStyle>
        )
      }
}


export default CardAddOrder;
