import React,{useState} from 'react'
import { styled } from '@material-ui/core/styles';

import Card from '@material-ui/core/Card'
import CardHeader from '@material-ui/core/CardHeader'
import IDataBufferVSM from '../Data/IDataBufferVSM';
import CardContent from '@material-ui/core/CardContent'
import Typography from '@material-ui/core/Typography'

import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import {IconButton} from '@material-ui/icons';

interface ExpandMoreProps extends IconButtonProps {
    expand: boolean;
  }



const Buffer = (props:{buffer: IDataBufferVSM}) => {

    const Colors = { RED: 'red', GREEN: 'green', YELLOW: 'yellow', WHITE: 'white', Header: '#6495ed'};

    const 
    {
        name
    } = props.buffer

    const ExpandMore = styled((props: ExpandMoreProps) => {
        const { expand, ...other } = props;
        return <IconButton {...other} />;
      })(({ theme, expand }) => ({
        transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      }));

    const [expanded, setExpanded] = React.useState(false);
    const handleExpandClick = () => {
        setExpanded(!expanded);
    };

    return(
        <Card sx={{ maxWidth: 200 }}>
            <CardHeader
                action={
                    <IconButton aria-label="settings">
                    <MoreVertIcon />
                    </IconButton>
                }
                title={name}
                subheader="Время цикла: "
                sx={{fontWeight: 'bold', color:'white', backgroundColor: Colors.GREEN}}>
            </CardHeader>
            <CardContent>
                <Typography variant="body2" color="text.secondary">
                This impressive paella is a perfect party dish and a fun meal to cook
                together with your guests. Add 1 cup of frozen peas along with the mussels,
                if you like.
                </Typography>
            </CardContent>
            <ExpandMore
                expand={expanded}
                onClick={handleExpandClick}
                aria-expanded={expanded}
                aria-label="show more"
            >
                <ExpandMoreIcon />
            </ExpandMore>
        </Card>
    )

}


export default Buffer;
