import React,{useState} from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { CardHeader, IconButton } from '@material-ui/core';
import SettingsIcon from '@material-ui/icons/Settings';
import { Divider } from '@mui/material';

import {IDataColorOrderInDefault} from '../../Data/DataCardOrderInDefault/IDataColorOrderInDefault'

enum simulationEnum{
  Start = 'Запустить симуляцию"',
  Stop = 'Остановить симуляцию"'
}
const CardOrderInDefault = (props:{OrderColor: IDataColorOrderInDefault}) => {

  const [simulation, setSimulation] = useState({
    text: '',
    work: false
  });

  const {Start,Stop} = simulationEnum;

  const onSimulation = () => {

    if(simulation.work)
    {
      setSimulation({...simulation, work:!simulation.work, text: Stop })
    }
    else
    {
      setSimulation({...simulation, work:!simulation.work, text: Start })
    }
  }

  return (
    <Card sx={{ minWidth: 200,color: 'white',  backgroundColor: props.OrderColor }}>
      <CardHeader
        titleTypographyProps={{ variant: "h6" }}
        action={
          <IconButton aria-label="settings">
            <SettingsIcon />
          </IconButton>
        }
        title="Заказ номер: №43352"
        subheader={<Typography style={{color: 'white'}}>23:24:23</Typography>}
      />
      <Divider/>
      <CardActions>
        <Button size="small" variant={simulation ? "contained":"outlined"} onClick={onSimulation}>{simulation.text}</Button>
      </CardActions>
    </Card>
  );
}

export default CardOrderInDefault;
