import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import { useOrdersType } from '../../type/useOrdersType'


const initialState: useOrdersType ={
    orders: [],
    loading: false,
    error: null
}

const useOrdersReducer = createSlice({  
    name: 'orders',  
    initialState,  
    reducers: {       
        fetch_orders(state) {      
            state.loading = true   
        },
        fetch_orders_success(state,action) {      
            state.orders = action.payload; 
        },     
        fetch_orders_error(state, action: PayloadAction<number>) {      
           // state.value += action.payload    
        },  
    },
})
export default useOrdersReducer.reducer
export const { fetch_orders, fetch_orders_success, fetch_orders_error } = useOrdersReducer.actions
