import * as React from 'react';
import { DataGridPro } from '@mui/x-data-grid-pro';
import { DataGrid, GridColDef, GridValueGetterParams } from '@mui/x-data-grid';
import moment from 'moment';
import Order_view from '../Data/IDataOrder/IDataOrder';




const DemoArchive = (props:{orders: Order_view[]}) => {



  const columns: GridColDef[] = [
    { field: 'id', headerName: '№', width: 90 },
    {
      field: 'orderName',
      headerName: 'Заказ',
      width: 150,
      editable: true,
    },
    {
      field: 'limitation',
      headerName: 'Сроком до',
      width: 150,
      editable: true,
    },
    {
      field: 'tPlan',
      headerName: 'Плановое',
      //type: 'number',
      width: 110,
      editable: true,
    },
    {
      field: 'fullName',
      headerName: 'Full name',
      description: 'This column has a value getter and is not sortable.',
      sortable: false,
      width: 160,
      valueGetter: (params: GridValueGetterParams) =>
        `${params.getValue(params.id, 'orderName') || ''} ${
          params.getValue(params.id, 'limitation') || ''
        }`,
    },
  ];

  const convertToTime = (t: number) =>
  {
    return moment.unix(t).utc().format('H:m:s')
  }

  const rows = [
    { id: 1, limitation: 'Snow', orderName: 'Jon', tPlan: convertToTime(35) },
    { id: 2, limitation: 'Lannister', orderName: 'Cersei', tPlan: convertToTime(42) },
    { id: 3, limitation: 'Lannister', orderName: 'Jaime', tPlan: convertToTime(45) },
    { id: 4, limitation: 'Stark', orderName: 'Arya', tPlan: convertToTime(16) },
    { id: 5, limitation: 'Targaryen', orderName: 'Daenerys', tPlan: null },
    { id: 6, limitation: 'Melisandre', orderName: null, tPlan: convertToTime(150) },
    { id: 7, limitation: 'Clifford', orderName: 'Ferrara', tPlan: convertToTime(44) },
    { id: 8, limitation: 'Frances', orderName: 'Rossini', tPlan: convertToTime(36) },
    { id: 9, limitation: 'Roxie', orderName: 'Harvey', tPlan: convertToTime(65) },
  ];


  return (
    <div style={{ height: 520, width: '100%' }}>
      <DataGrid
        rows={props.orders}
        columns={columns}
        pageSize={5}
        rowsPerPageOptions={[5]}
        checkboxSelection
        disableSelectionOnClick
      />
    </div>
  );
}
export default DemoArchive;
