import moment from 'moment';
import React,{useState} from 'react'
import IDataOrder from './Data/IDataOrder';

import PauseIcon from '@material-ui/icons/Pause';
import { IconButton } from '@material-ui/core';

interface IDataHeader{
    name?: string,
    TActual: number,
    TPlan: number
}

//IDataHeader

const CustomizedAccordiongsHeader = (props:{data: IDataOrder})  => 
{
    const {
        name,
        tActual,
        tPlan} = props.data;

    const [bad,setBad] = useState(false);
    const [textPeriod,setTextPeriod] = useState("");

    let timeTActual = moment.unix(tActual).utc().format('H:m:s')
    let timeTPlan = moment.unix(tPlan).utc().format('H:m:s')



    const ConvertPeriodToString = (timePeriod: any)=>
    {
        var list = timePeriod.split(' ')

        let text = timePeriod;
        //console.log(list);

        if(list[0] === 'in')
        {


            if(list[1] === 'a')
            {
                text = "Отставание на несколько сукунд"
                if(list[2] === 'minute')
                {
                    text = "Отставание на 1 м"
                }

            }
            else
            {
                let t =list[1];
                text = "Отставание на "+ t

                switch(list[2])
                {
                    case "hours": 
                        text += " ч"
                    break;
                    case "minutes": 
                        text += " м"
                    break;

                }
            }
           // console.log(text)
        }
        else
        {
            if(list[0] === 'a')
            {
                text = "В запасе много времени"
                if(list[2] === 'minute')
                {
                    text = "Отставание на 1 м"
                }

            }
            else
            {
                text = "В запасе " + list[0];
                switch(list[1])
                {
                    case "hours": 
                        text += " ч"
                    break;
                    case "minutes": 
                        text += " м"
                    break;

                }
            }

        }

        if(textPeriod != text)
        {
            setTextPeriod(text);
        }

    }

  //var diff = moment("23:07").unix() - moment("22:07").unix();

  let timePeriod = '';
  let text = '';
  if(tPlan - tActual  === 0)
  {
    timePeriod  = moment.unix(tPlan-tActual).utc().format('H:m:s');
    text = '';
  }
  else
  {
    if(tPlan - tActual > 0)
    {
           timePeriod   = moment.unix(tPlan-tActual).utc().format('H:m:s');
           text = 'Осталось';
    }
    else
    {
          timePeriod   = moment.unix(tActual-tPlan).utc().format('H:m:s');
          text = 'Отставание на';
    }
  }



// let timePeriod =  moment.unix(tPlan).from(moment.unix(tActual))   //ConvertPeriodToString( moment.unix(tActual).utc().from(moment.unix(tPlan).utc()) )


    if(tActual > tPlan && bad === false)
    {
        setBad(true);
    }

    if(tActual < tPlan && bad)
    {
        setBad(false);
    }


    return (
        <div style={{display: 'grid', gridTemplateColumns: '100px auto',}}>
                <p>{name}</p>
                <div style={{display: 'grid', gridTemplateColumns: '1fr 1fr 1fr'}}>
                <div style={{display: 'flex', justifyContent:'space-around'}} >
                    <p><PauseIcon style={{color: ''}}/></p>
                    <p>Актуальное вермя</p>
                    <p style ={{backgroundColor: bad ? 'red': '', color: bad ? "white": "" }}>{timeTActual}</p>
                  </div>
                  <div style={{display: 'flex', justifyContent:'space-around'}} >
                    <p>{text}</p>
                    <p>{timePeriod}</p>
                  </div>
                  <div style={{display: 'flex', justifyContent:'space-around'}} >
                    <p>Плановое время</p>
                    <p style ={{backgroundColor: bad ? 'red': '', color: bad ? "white": "" }}>{timeTPlan}</p>
                  </div>
                </div>
        </div>
    )
}


export default CustomizedAccordiongsHeader;
