import styled from 'styled-components';
import { device } from './device';

const AppStyle = styled.div`
  margin: auto;
  font-family: "sans-serif";
  text-align: center;

  @media ${device.laptopL} { 
    max-width: ${device.laptopL};
  }

  @media ${device.desktopL} {
    max-width: 2560px;
    text-align: right;
  }
`;


export default AppStyle;
