import React from 'react';

import ListItem from "./ListItem"

import  {ListActiveConsumer} from "../../ListActiveContextProvider"

class ListView extends React.PureComponent {
    constructor(props) {
      super(props);

      this.state = {
        selected: false
      }
    }




    _showChoices = (context) => {
      return context.ListActive.map((item, i) => {
        return (
            <ListItem
                title={item.choice_name}
                checked={item.isChecked}
                size={50}
                onIconPress={() => {}}
                onPress={() => {


                context.ListActive.map((item2,j) =>{
                  item2.isSelected = false;
                });

                item.isSelected = true;
                this.setState({ selected: !this.state.selected });
                console.log("this.state.selected",this.state.selected)
                }}
                isSelected={item.isSelected}
                key={item.choice_id}
                MyKey = {item.choice_id}
            />
        );
      });
    };

    componentDidMount()
    {

    }

    render() {
      return (
        <div>
          <ListActiveConsumer>
          {context => (
            <ul>{this._showChoices(context)}</ul>
          )}
          </ListActiveConsumer>  
        </div>
      );
    }
  }



  export default ListView;
