    actualAvailability: number,
    defaultAvailability: number,
    parallel: boolean,
    users: User_view[]
}

interface Buffer_view
{
    name: string,
    type: string,
    minHold: number,
    max: number,
    value: number,
    valueDefault: number,      
    replenishmentSec: number,
    replenishmentCount: number
}

interface SectionEtap_view
{
    buffer_view: Buffer_view,
    etap_view: Etap_view
}


interface SectionLogic_view
{
    sectionEtap_views: SectionEtap_view[]
}

//Модель данных для отображения
interface Card_view
{
    name: string,
    sectionLogic_views: SectionLogic_view[]
}



/////////




interface TabPanelProps {
    children?: React.ReactNode;
    index: number;
    value: number;
  }

  function TabPanel(props: TabPanelProps) {
    const { children, value, index, ...other } = props;

    return (
      <div
        style= {{width: '100%'}}
        role="tabpanel"
        hidden={value !== index}
        id={`vertical-tabpanel-${index}`}
        aria-labelledby={`vertical-tab-${index}`}
        {...other}
      >
        {value === index && (
          <Box sx={{ p: 3 }}>
            <Typography>{children}</Typography>
          </Box>
        )}
      </div>
    );
  }

  function a11yProps(index: number) {
    return {
      id: `vertical-tab-${index}`,
      'aria-controls': `vertical-tabpanel-${index}`,
    };
  }




