import Triangle from "../../../SVG/Triangle/Triangle"

class MidleContent extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          scale: 1,
          x: 0,
          y: 0 
        };
      }

      static MidleContentStyle;

      //static contextType = ListActiveConxtext;

      _showCard = () =>
      {
          if(this.context.ListActive.length === 0)
          {
            return <h3>MidleContent</h3>
          }

          let object = this.context.ListActive.find(item => item.isSelected === true)

          console.log("this._showCard(context)",this.context)



          return <h3>{object.name}</h3>
      }


      handleOnWhell = (e) =>
      {
        let count = (e.deltaY/17)
        console.log("sdf")

        let target = this.MidleContentStyle//e.target.closest('....'); // Здесь что-то уникальное, что может указать на род. блок
        /*if(e.target.style.position === "relative" )
        {
             target = e.target.parentElement
        }*/


        let targetCoords = target.getBoundingClientRect();
        let xCoord = e.clientX - targetCoords.left;
        let yCoord = e.clientY - targetCoords.top;


        //console.log(this.MidleContentStyle)

        console.log("xCoord = ",xCoord," yCoord = ",yCoord)

        switch (e.deltaY > 0) {
            case true:
              console.log('positive',e.deltaY,count);
              let plus = this.state.scale+0.05

              this.setState({scale: plus,x: xCoord, y: yCoord}) //,x: xCoord, y: yCoord
              break;
            default:
              console.log('negative',e.deltaY,count);
              count *=(-1)

              let minus = this.state.scale-0.05
              if(minus < 0 )
              {
                minus = 0
              }
              this.setState({scale: minus}) //,x: e.screenX, y: e.screenY
          }
      }

      handleOnMouseMove = (e) =>
      {

        //console.log('Координаты по X: ' + xCoord,'Координаты по Y: ' + yCoord);
        //this.setState({ x: e.screenX, y: e.screenY });
      }

      render() {
        return (
            <MidleContentStyle ref={(MidleContentStyle) => { this.MidleContentStyle = MidleContentStyle; }} onWheel={this.handleOnWhell} onMouseMove={this.handleOnMouseMove}>
               {/* <div style={{width: "100%", height: "100%",position: "relative",transform:"translate("+this.state.x+"px,"+this.state.y+"px) scale("+this.state.scale+")",boxShadow: "rgba(0,0,0,0.2) 0px 3px 5px -1px,rgba(0,0,0,0.14) 0px 6px 10px 0px,rgba(0,0,0,0.12) 0px 1px 14px 0px"}}>
                  <div  style={{width: "100%", height: "100%",position: "absolute"}}>
                    <Triangle   left={0} top={0} width={100} height={100} min={50} max={100} value={0}/>
                    <Triangle   left={200} top={0}  width={100} height={100} min={50} max={100} value={50}/>
                  </div>
        </div>*/}
               {/* <DiagramViewBox/>

              <ListActiveConxtext.Consumer>
                {value => (
                  <h3>MidleContent {console.log(value.ListActive)}</h3>
                )}

                </ListActiveConxtext.Consumer>*/} 
            </MidleContentStyle>

        )
      }
}



export default MidleContent;

