import * as React from 'react';
import Button from '@material-ui/core/Button';
import Avatar from '@material-ui/core/Avatar';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Dialog from '@material-ui/core/Dialog';
import PersonIcon from '@material-ui/icons/Person';
import AddIcon from '@material-ui/icons/Add';
import Typography from '@material-ui/core/Typography';
import { blue } from '@material-ui/core/colors';

import TextField from '@material-ui/core/TextField';

import AddCircleIcon from '@material-ui/icons/AddCircle';
import Transition from './Transition'
import DialogActions from '@material-ui/core/DialogActions';

import IDialogAddOrder from '../Data/Dialog/IDialogAddOrder'
import IDialogAddOrderItem from '../Data/Dialog/IDialogAddOrderItem'
import IconButton from '@mui/material/IconButton';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';

export interface SimpleDialogProps {
  open: boolean;
  selectedValue: IDialogAddOrder;
  onClose: (value: IDialogAddOrder) => void;
  addOrder: (value: IDialogAddOrder) => void
}



function SimpleDialog(props: SimpleDialogProps) {
  const { onClose, selectedValue, open,addOrder } = props;

  const [newOrder, setNewOrder] = React.useState<IDialogAddOrderItem[]>();

  let item = new Object as IDialogAddOrder;

  const handleClose = () => {
    onClose(selectedValue);


    item.orders = newOrder as IDialogAddOrderItem[]

    //console.log("dfd = ",newOrder);
    addOrder(item)
  };




  return (
    <Dialog open={open}>
      <DialogTitle>Формирование заказа</DialogTitle>
      <Transition setNewOrder={setNewOrder}/>
      <DialogActions>
          <Button onClick={handleClose}>Disagree</Button>
          <Button onClick={handleClose} autoFocus>
            Agree
          </Button>
      </DialogActions>
    </Dialog>
  );
}

export default function DialogAddOrder(props:{addOrder: (value: IDialogAddOrder) => void}) {
  const [open, setOpen] = React.useState(false);
  const [selectedValue, setSelectedValue] = React.useState();

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };



  return (
    <div style={{mr: 2, left: '49.5%'}}>
      <IconButton 
                color="inherit"
                aria-label="open drawer"
                edge="start"
                sx={{ mr: 2, left: '49.5%'}}>
                <AddCircleOutlineIcon/>
      </IconButton>
      <Button 
      onClick={handleClickOpen}
      style={{width: '100%',display:'flext', justifyContent: 'space-around'}}
      variant="contained" endIcon={<AddCircleIcon />}>
        Добавить заказ
      </Button>

      <SimpleDialog
        selectedValue={selectedValue as any}
        open={open}
        onClose={handleClose}
        addOrder={props.addOrder}
      />
    </div>
  );
}

