import React, { useState, useEffect, useRef,useCallback } from "react";

const URL = 'ws://192.168.0.2:5001/ws';


const Chat =() => {
	const [user, setUser] = useState('Tarzan');
  	const [message, setMessage] = useState([]);
  	//const [ws, setWs] = useState(null);




    const soket = useRef(null)
    //let ws = useRef(null);

  /*	useEffect(() => {
	    soket.onopen = () => {
	      console.log('WebSocket Connected');
	    }

	    soket.onmessage = (e) => {
          console.log('Read');
	      const message = JSON.parse(e.data);
	      setMessages([message, ...messages]);
	    }

	    return () => {
            soket.onclose = () => {
	        console.log('WebSocket Disconnected');
	        setWs(new WebSocket(URL));
	      }
	    }
  	}, ); //[ws.onmessage, ws.onopen, ws.onclose, messages]
*/

const [isPaused, setIsPaused] = useState(false);
const [data, setData] = useState(null); 
const [status, setStatus] = useState("");
const ws = useRef(null);

useEffect(() => {
    if (!isPaused) {
        console.log('rerender useEffect = ', ws)
        if(ws.current === null)
        {
            ws.current = new WebSocket(URL); // создаем ws соединение
            ws.current.onopen = () => setStatus("Соединение открыто");	// callback на ивент открытия соединения
            ws.current.onclose = () => setStatus("Соединение закрыто"); // callback на ивент закрытия соединения

            gettingData();
        }
    }
    else
    {
        ws.current.close();
    }

    //return () => ws.current.close(); // кода меняется isPaused - соединение закрывается
}, [ws, isPaused]);

const gettingData = useCallback(() => {
    if (!ws.current) return;

    ws.current.onmessage = e => {                //подписка на получение данных по вебсокету
        if (isPaused) return;
        const message = JSON.parse(e.data);
        setMessage(message);
    };
}, [isPaused]);

      console.log('rerender')

  	return (
	    <div>
	        <label htmlFor="user">
	          Name :
	          <input
	            type="text"
	            id="user"
	            placeholder="User"
	            value={user}
	            onChange={e => setUser(e.target.value)}
	          />
	        </label>

	        <ul>
	          {message.map((message, index) =>
	            <li key={index}>
	              <b>{message.user}</b>: <em>{message.message}</em>
	            </li>
	          )}
	        </ul>

	        <form
	          action=""
	          onSubmit={e => {
	            e.preventDefault();

	            setMessage([]);
	          }}
	        >
	          <input
	            type="text"
	            placeholder={'Type a message ...'}
	            value={message}
	            onChange={e => setMessage(e.target.value)}
	          />
	          <input type="submit" value={'Send'} />
	        </form>
            <button onClick={() => setIsPaused(!isPaused)}>
                {isPaused ? "Resume" : "Pause"}
            </button>
	    </div>
  	)
}

export default Chat;
