import React from "react";
import { 
  DiagramComponent,
  HierarchicalTree,
  MindMap,
  RadialTree,
  ComplexHierarchicalTree,
  DataBinding,
  Snapping,
  PrintAndExport,
  BpmnDiagrams,
  SymmetricLayout,
  ConnectorBridging,
  UndoRedo,
  LayoutAnimation,
  DiagramContextMenu,
  ConnectorEditing,
  Inject,
  SnapConstraints,
  DiagramTools,
  NodeConstraints
} from "@syncfusion/ej2-react-diagrams";

import { AccumulationChartComponent, AccumulationSeriesCollectionDirective, AccumulationSeriesDirective, AccumulationDataLabel, PieSeries, AccumulationLegend, AccumulationTooltip } from '@syncfusion/ej2-react-charts';



let diagramInstance = DiagramComponent;


let lineChart;
let columnChart;
let pie;
let lGrid;
let exp = expenseData;
let predicateStart;
let predicateEnd;
let predicate;
let gtemplate = gridTemplate;


class Diagram extends React.PureComponent {
    constructor(props) {
        super(props);
        this.state = {
          myStyle: props.myStyle,
          diagram: props.diagram,
          connectors: props.connectors,
        };


      }
      diagramInstance =  diagramInstance;
      // A node is created and stored in nodes array.


      async api(ip,port,command) {
        // GET request using fetch with async/await
        const response = await fetch("http://"+ip+":"+port+command);
        const data = await response.json();
        return data
      }


      async create_customer(element)
      {
        let http = "http://"+window.ip+":"+window.port
        console.log("element",element.oldProperties.id);

        switch(element.oldProperties.id)
        {
          case "Customer":{
            //api/reason_customer/insert/:Node_name_id/:offsetX/:offsetY"

            //const response = await fetch(http+"/api/reason_customer/insert/"+element.properties.id+"/"+element.properties.offsetX+"/"+element.properties.offsetY)
            //const data = await response;
            //return data
          }
          case "Process":{
            break;
          }
          case "External_Shipment":{
            break;
          }
          case "Inventory_customer_supplier":
          {
              break;
          }
          case "Production_control":
          {
              break;
          }
        }



      }

     update_position(element)
      {
        var myData = []
        element.map(item =>{
          myData.push({
            id: item.properties.id,
            offsetX: item.properties.offsetX,
            offsetY: item.properties.offsetY
          })
        })

        //console.log(JSON.stringify(myData))
        //api/reason_customer/insert/:Node_name_id/:offsetX/:offsetY"
        let http = "http://"+window.ip+":"+window.port
        //fetch(http+"/api/vsm/update_position/")
        //.then(response => response.json());
      }



      diagramTemplate =(props) =>{
        if (props.id === "pie") {
            return (
            <div id="diagram_control" className="diagram_border_cus">
            <div className="pane col-xs-12 col-sm-12 col-md-12 pie-container">
              <div className="pieChartHeader">
                <p className="chart-title">Total Expenses</p>
                <p id="rangeDate" className="chart-value">Jun 1 - Dec 1</p>
              </div>
              <div id="pieChart" style={{ height: '100%', width: '49%', overflow: 'hidden', float: 'left' }}>
                <AccumulationChartComponent style={{ display: 'block' }} ref={pies => (pie = pies)} id='pieChart' width='100%' height='350px' legendSettings={acclegendSettings} enableSmartLabels={true} textRender={onTextRender} animationComplete={onAnimateCompleted} loaded={acconChartLoaded}>
                  <Inject services={[PieSeries, AccumulationLegend, AccumulationDataLabel, AccumulationTooltip]}/>
                  <AccumulationSeriesCollectionDirective>
                    <AccumulationSeriesDirective xName='text' yName='y' startAngle={0} legendShape='Circle' endAngle={360} innerRadius='50%' radius='83%' groupTo={groupValue} palettes={colorPalettes} dataLabel={dataLabel} dataSource={pieRenderingData} animation={accanimation}>
                    </AccumulationSeriesDirective>
                  </AccumulationSeriesCollectionDirective>
                </AccumulationChartComponent>
              </div>
              <div id="grid" style={{ height: '100%', width: '49%', overflow: 'hidden', float: 'left' }}>
                <GridComponent id="legend-grid" ref={lGrids => (lGrid = lGrids)} dataSource={pieRenderData} load={onGridLoad} style={{ boxShadow: 'none' }} rowTemplate={gtemplate} dataBound={onGridDataBound}>
                  <Inject services={[Page, RowDD, Toolbar, ColumnChooser, DetailRow, ColumnMenu, Selection, Edit, Sort, Group]}/>
                  <ColumnsDirective>
                    <ColumnDirective width='10%' textAlign="Center"/>
                    <ColumnDirective width='50%'/>
                    <ColumnDirective width='20%'/>
                    <ColumnDirective width='20%'/>
                  </ColumnsDirective>
                </GridComponent>
              </div>
            </div>
          </div>);
        }
    }




      render() {

        const {myStyle} = this.state


        return (
        <DiagramComponent  id="diagram" ref={diagram => (diagramInstance = diagram)}
        width = {'100%'}
        height = {'100%'}
        snapSettings={{ constraints: SnapConstraints.None }}
        // Add node
        nodes = {
          this.state.diagram
        }
        connectors = {
          this.state.connectors
        }

        drop={(e)=>{
          //console.log("drop",e)
          //console.log("drop length",e.element.annotations.length)
          this.create_customer(e.element)
          //e.element.annotations[0].content = "#1"

          //e.newValue[0].annotations[0].content = "#1"
        }}

        positionChange={(e)=>{

          switch(e.state)
          {
            case "Completed":{
              console.log("position",e.source.nodes)
              if(e.source.nodes !== undefined)
                this.update_position(e.source.nodes)

            break;  }
            case "Progress":{break;}
            case "Start":{break;}
          }



        }}

        dragTargetEnd={(e)=>{
          console.log("dragTargetEnd",e)
        }}


        tool = {
          DiagramTools.ZoomPan
      }


       /* selectionChange={(e)=>{

          console.log("SELECT",e.newValue)
        }}*/
        created = {
          () => {

            //console.log(diagramInstance.getActiveLayer())
            //this.diagramInstance.getActiveLayer();


           // diagramInstance.tool = DiagramTools.ZoomPan;
            //diagramInstance.dataBind();
            diagramInstance.scrollChange = args => {
            //Obtains the pan status
            let panStatus = args.panState;
           }


            //diagramInstance.tool = DiagramTools.ZoomPan;
            //diagramInstance.dataBind();
            /*diagramInstance.scrollChange = { args: IScrollChangeEventArgs => {
            //Obtains the pan status
            console.log(IScrollChangeEventArgs)
            let panStatus = IScrollChangeEventArgs.panState;}}
            }*/
          }
        }

            //Disables mouse over tooltip at runtime
            tooltip = {
              null
          }


          style = {myStyle}
          nodeTemplate={this.diagramTemplate}


       >
         <Inject  width = {'200px'}
        height = {'200px'}
  services={[
    HierarchicalTree,
    MindMap,
    RadialTree,
    ComplexHierarchicalTree,
    DataBinding,
    Snapping,
    PrintAndExport,
    BpmnDiagrams,
    SymmetricLayout,
    ConnectorBridging,
    UndoRedo,
    LayoutAnimation,
    DiagramContextMenu,
    ConnectorEditing
  ]}
/>
       </DiagramComponent>);
      }
}


export default Diagram;
